import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import {
  Container,
  Box,
  Typography,
  Grid,
  Paper,
  Card,
  CardContent,
  Divider,
  Chip,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  CircularProgress,
  Button,
  IconButton,
  Tooltip,
  Tab,
  Tabs,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Slider,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@mui/material";
import HomeButton from "./HomeButton";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip as RechartsTooltip,
  Legend,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
  LineChart,
  Line,
} from "recharts";
import useDebounce from "../hooks/useDebounce";

// Simulated data for dashboard
const facilityData = [
  {
    name: "Tampa General Hospital",
    state: "FL",
    hazardLevel: "High",
    readinessScore: 85,
    bedCapacity: 1011,
    emergencyStaff: 287,
  },
  {
    name: "Memorial Hermann Hospital",
    state: "TX",
    hazardLevel: "Very High",
    readinessScore: 92,
    bedCapacity: 657,
    emergencyStaff: 198,
  },
  {
    name: "Orlando Regional Medical Center",
    state: "FL",
    hazardLevel: "High",
    readinessScore: 79,
    bedCapacity: 808,
    emergencyStaff: 235,
  },
  {
    name: "Methodist Hospital",
    state: "TX",
    hazardLevel: "Very High",
    readinessScore: 81,
    bedCapacity: 573,
    emergencyStaff: 178,
  },
  {
    name: "North Florida Regional Medical Center",
    state: "FL",
    hazardLevel: "Extreme",
    readinessScore: 88,
    bedCapacity: 432,
    emergencyStaff: 156,
  },
  {
    name: "West Florida Hospital",
    state: "FL",
    hazardLevel: "High",
    readinessScore: 77,
    bedCapacity: 531,
    emergencyStaff: 167,
  },
  {
    name: "Bayshore Medical Center",
    state: "TX",
    hazardLevel: "High",
    readinessScore: 75,
    bedCapacity: 364,
    emergencyStaff: 122,
  },
  {
    name: "Doctors Hospital of Sarasota",
    state: "FL",
    hazardLevel: "Moderate",
    readinessScore: 82,
    bedCapacity: 155,
    emergencyStaff: 87,
  },
  {
    name: "Corpus Christi Medical Center",
    state: "TX",
    hazardLevel: "Very High",
    readinessScore: 84,
    bedCapacity: 413,
    emergencyStaff: 135,
  },
  {
    name: "Kendall Regional Medical Center",
    state: "FL",
    hazardLevel: "High",
    readinessScore: 80,
    bedCapacity: 417,
    emergencyStaff: 143,
  },
];

const hazardDistribution = [
  { name: "Extreme", value: 1 },
  { name: "Very High", value: 3 },
  { name: "High", value: 5 },
  { name: "Moderate", value: 1 },
  { name: "Low", value: 0 },
];

const stateDistribution = [
  { name: "Florida", value: 6 },
  { name: "Texas", value: 4 },
  { name: "Georgia", value: 0 },
  { name: "Louisiana", value: 0 },
  { name: "Other", value: 0 },
];

const monthlyIncidents = [
  { month: "Jan", incidents: 12 },
  { month: "Feb", incidents: 9 },
  { month: "Mar", incidents: 15 },
  { month: "Apr", incidents: 23 },
  { month: "May", incidents: 28 },
  { month: "Jun", incidents: 37 },
  { month: "Jul", incidents: 42 },
  { month: "Aug", incidents: 58 },
  { month: "Sep", incidents: 61 },
  { month: "Oct", incidents: 47 },
  { month: "Nov", incidents: 31 },
  { month: "Dec", incidents: 19 },
];

const resourceDistribution = [
  { name: "Beds Available", value: 4361 },
  { name: "Beds Occupied", value: 1500 },
];

// Color constants matching the app's theme
const COLORS = ["#FF6600", "#FF8533", "#FFA366", "#FFBF99", "#FFD9CC"];
const BLUE_COLORS = ["#003366", "#004080", "#004d99", "#0059b3", "#0066cc"];

function LocalDashboard() {
  const location = useLocation();
  const defaultZipCode = "33612"; // Default to a Florida zip code for HCA hospitals
  const zipCode = location.state?.zipCode || defaultZipCode;

  const [tabValue, setTabValue] = useState(0);
  const [localData, setLocalData] = useState([]);
  const [nearbyData, setNearbyData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [radius, setRadius] = useState(500); // Increased from 100 to 500 miles to capture more facilities
  const debouncedRadius = useDebounce(radius, 500);

  // Derived state for dashboard data
  const [facilityData, setFacilityData] = useState([]);
  const [stateDistribution, setStateDistribution] = useState([]);
  const [totalBeds, setTotalBeds] = useState(0);
  const [totalStaff, setTotalStaff] = useState(0);

  // Monthly incidents data - no real API for this, keeping simulated
  const monthlyIncidents = [
    { month: "Jan", incidents: 12 },
    { month: "Feb", incidents: 9 },
    { month: "Mar", incidents: 15 },
    { month: "Apr", incidents: 23 },
    { month: "May", incidents: 28 },
    { month: "Jun", incidents: 37 },
    { month: "Jul", incidents: 42 },
    { month: "Aug", incidents: 58 },
    { month: "Sep", incidents: 61 },
    { month: "Oct", incidents: 47 },
    { month: "Nov", incidents: 31 },
    { month: "Dec", incidents: 19 },
  ];

  // Hazard distribution - no real API for this, keeping simulated
  const hazardDistribution = [
    { name: "Extreme", value: 1 },
    { name: "Very High", value: 3 },
    { name: "High", value: 5 },
    { name: "Moderate", value: 1 },
    { name: "Low", value: 0 },
  ];

  // Resource distribution - will update based on real data
  const [resourceDistribution, setResourceDistribution] = useState([
    { name: "Beds Available", value: 4361 },
    { name: "Beds Occupied", value: 1500 },
  ]);

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  const handleRadiusChange = (event) => {
    setRadius(event.target.value);
  };

  // Fetch data from the API
  useEffect(() => {
    if (zipCode) {
      setLoading(true);
      console.log(
        `Fetching facilities with radius ${debouncedRadius} miles from ${zipCode}`
      );
      fetch(
        `http://localhost:8000/api/data/?zip_code=${zipCode}&radius=${debouncedRadius}`
      )
        .then((res) => res.json())
        .then((data) => {
          console.log("API response for dashboard:", data);
          console.log(
            `Received ${data.local ? data.local.length : 0} local and ${
              data.nearby ? data.nearby.length : 0
            } nearby records`
          );
          setLocalData(data.local || []);
          setNearbyData(data.nearby || []);
          processData([...(data.local || []), ...(data.nearby || [])]);
          setLoading(false);
        })
        .catch((err) => {
          console.error("Error fetching data for dashboard:", err);
          setLoading(false);
        });
    }
  }, [zipCode, debouncedRadius]);

  // Process API data to create dashboard data
  const processData = (combinedData) => {
    if (!combinedData || combinedData.length === 0) {
      // Set empty defaults if no data
      setFacilityData([]);
      setStateDistribution([]);
      setTotalBeds(0);
      setTotalStaff(0);
      setResourceDistribution([
        { name: "Beds Available", value: 0 },
        { name: "Beds Occupied", value: 0 },
      ]);
      return;
    }

    // Process facility data
    const facilities = processFacilityData(combinedData);
    setFacilityData(facilities);

    // Process state distribution
    const states = processStateDistribution(facilities);
    setStateDistribution(states);

    // Calculate totals
    const beds = facilities.reduce(
      (sum, facility) => sum + (facility.bedCapacity || 0),
      0
    );
    setTotalBeds(beds);

    const staff = facilities.reduce(
      (sum, facility) => sum + (facility.emergencyStaff || 0),
      0
    );
    setTotalStaff(staff);

    // Update resource distribution
    setResourceDistribution([
      { name: "Beds Available", value: Math.round(beds * 0.74) }, // Assuming 74% availability
      { name: "Beds Occupied", value: Math.round(beds * 0.26) },
    ]);
  };

  // Group data by location to get facility-level data
  const processFacilityData = (data) => {
    const locationMap = new Map();

    // Make sure we have data to process
    if (!data || data.length === 0) {
      console.log("No data to process in processFacilityData");
      return [];
    }

    console.log(
      `Processing ${data.length} person records to extract facilities`
    );

    // Group by location
    data.forEach((person) => {
      // Skip entries without a location
      if (!person.location) {
        return;
      }

      // Create a unique key for the facility
      // Combine location with coordinates to ensure uniqueness
      const facilityKey = `${person.location}_${person.zip_code || ""}`;

      if (!locationMap.has(facilityKey)) {
        locationMap.set(facilityKey, {
          name: person.location,
          state: person.state,
          city: person.city,
          zip_code: person.zip_code,
          latitude: person.latitude,
          longitude: person.longitude,
          staff: [],
        });
      }

      locationMap.get(facilityKey).staff.push(person);
    });

    console.log(
      `Grouped ${data.length} person records into ${locationMap.size} unique facilities`
    );

    // Convert the map to an array and calculate derived properties
    return Array.from(locationMap.values()).map((facility) => {
      // Count medical staff
      const medicalStaff = facility.staff.filter(
        (person) => person.is_medical === "Medical"
      ).length;

      // Assign a hazard level based on state (simulated)
      const hazardLevel =
        facility.state === "FL"
          ? Math.random() > 0.5
            ? "High"
            : "Very High"
          : facility.state === "TX"
          ? Math.random() > 0.7
            ? "Very High"
            : "High"
          : facility.state === "LA"
          ? "Extreme"
          : facility.state === "GA"
          ? "Moderate"
          : "Low";

      // Assign a readiness score (simulated, but based on staff count)
      const readinessScore = Math.max(70, Math.min(95, 75 + medicalStaff / 10));

      // Estimate bed capacity based on staff (simulated)
      const bedCapacity = Math.round(medicalStaff * 3.5);

      return {
        name: facility.name || "Unknown Facility",
        state: facility.state || "Unknown",
        city: facility.city || "Unknown",
        zip_code: facility.zip_code,
        latitude: facility.latitude,
        longitude: facility.longitude,
        hazardLevel,
        readinessScore: Math.round(readinessScore),
        bedCapacity,
        emergencyStaff: medicalStaff,
        totalStaff: facility.staff.length,
      };
    });
  };

  // Count facilities by state
  const processStateDistribution = (facilities) => {
    const stateCounts = {};

    facilities.forEach((facility) => {
      stateCounts[facility.state] = (stateCounts[facility.state] || 0) + 1;
    });

    // Convert to the required format for the pie chart
    return Object.entries(stateCounts)
      .map(([state, count]) => ({
        name: getStateName(state),
        value: count,
      }))
      .sort((a, b) => b.value - a.value);
  };

  // Convert state abbreviation to full name
  const getStateName = (stateCode) => {
    const states = {
      AL: "Alabama",
      AK: "Alaska",
      AZ: "Arizona",
      AR: "Arkansas",
      CA: "California",
      CO: "Colorado",
      CT: "Connecticut",
      DE: "Delaware",
      FL: "Florida",
      GA: "Georgia",
      HI: "Hawaii",
      ID: "Idaho",
      IL: "Illinois",
      IN: "Indiana",
      IA: "Iowa",
      KS: "Kansas",
      KY: "Kentucky",
      LA: "Louisiana",
      ME: "Maine",
      MD: "Maryland",
      MA: "Massachusetts",
      MI: "Michigan",
      MN: "Minnesota",
      MS: "Mississippi",
      MO: "Missouri",
      MT: "Montana",
      NE: "Nebraska",
      NV: "Nevada",
      NH: "New Hampshire",
      NJ: "New Jersey",
      NM: "New Mexico",
      NY: "New York",
      NC: "North Carolina",
      ND: "North Dakota",
      OH: "Ohio",
      OK: "Oklahoma",
      OR: "Oregon",
      PA: "Pennsylvania",
      RI: "Rhode Island",
      SC: "South Carolina",
      SD: "South Dakota",
      TN: "Tennessee",
      TX: "Texas",
      UT: "Utah",
      VT: "Vermont",
      VA: "Virginia",
      WA: "Washington",
      WV: "West Virginia",
      WI: "Wisconsin",
      WY: "Wyoming",
    };
    return states[stateCode] || stateCode;
  };

  return (
    <Container
      maxWidth="xl"
      sx={{
        width: "100%",
        minHeight: "100vh",
        backgroundColor: "#003366", // Dark blue background
        pt: 4,
        pb: 4,
        position: "relative",
      }}
    >
      <HomeButton />
      <Box sx={{ textAlign: "center", mb: 4 }}>
        <Typography variant="h4" sx={{ color: "#ffffff", fontWeight: 600 }}>
          Disaster Preparedness Dashboard
        </Typography>
        <Typography variant="subtitle1" sx={{ color: "#f8f8f8", mb: 2 }}>
          Real-time monitoring and status of HCA facilities in high-risk areas
        </Typography>
        <Box
          sx={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            mb: 2,
          }}
        >
          <Typography variant="subtitle2" sx={{ color: "#f8f8f8", mr: 2 }}>
            Showing data for zip code: {zipCode}
          </Typography>
          <FormControl
            variant="outlined"
            size="small"
            sx={{
              minWidth: 150,
              "& .MuiOutlinedInput-root": {
                color: "white",
                "& fieldset": { borderColor: "#FF6600" },
                "&:hover fieldset": { borderColor: "#FF6600" },
                "&.Mui-focused fieldset": { borderColor: "#FF6600" },
              },
              "& .MuiInputLabel-root": { color: "#FF6600" },
            }}
          >
            <InputLabel id="radius-select-label">Search Radius</InputLabel>
            <Select
              labelId="radius-select-label"
              id="radius-select"
              value={radius}
              onChange={handleRadiusChange}
              label="Search Radius"
            >
              <MenuItem value={50}>50 miles</MenuItem>
              <MenuItem value={100}>100 miles</MenuItem>
              <MenuItem value={200}>200 miles</MenuItem>
              <MenuItem value={500}>500 miles</MenuItem>
              <MenuItem value={1000}>1000 miles</MenuItem>
              <MenuItem value={2000}>2000 miles</MenuItem>
            </Select>
          </FormControl>
        </Box>
      </Box>

      {/* Loading indicator */}
      {loading && (
        <Box sx={{ display: "flex", justifyContent: "center", my: 4 }}>
          <CircularProgress sx={{ color: "#FF6600" }} />
        </Box>
      )}

      {!loading && (
        <>
          {/* Dashboard Tabs */}
          <Box sx={{ mb: 3 }}>
            <Tabs
              value={tabValue}
              onChange={handleTabChange}
              centered
              sx={{
                "& .MuiTab-root": { color: "#f8f8f8" },
                "& .Mui-selected": { color: "#FF6600" },
                "& .MuiTabs-indicator": { backgroundColor: "#FF6600" },
              }}
            >
              <Tab label="Overview" />
              <Tab label="Facility Status" />
              <Tab label="Resource Allocation" />
            </Tabs>
          </Box>

          {/* Tab 1: Overview */}
          {tabValue === 0 && (
            <Grid container spacing={3}>
              {/* Key Statistics Cards */}
              <Grid item xs={12}>
                <Grid container spacing={3}>
                  <Grid item xs={12} sm={6} md={3}>
                    <Card sx={{ backgroundColor: "#004080", color: "#ffffff" }}>
                      <CardContent sx={{ textAlign: "center" }}>
                        <Typography
                          variant="h6"
                          gutterBottom
                          sx={{ color: "#ffffff" }}
                        >
                          Total Facilities
                        </Typography>
                        <Typography
                          variant="h3"
                          sx={{ color: "#FF6600", fontWeight: "bold" }}
                        >
                          {facilityData.length || 0}
                        </Typography>
