// frontend/src/components/ZipCodeEntry.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Container, Box, Typography, TextField, Button } from '@mui/material';

function ZipCodeEntry() {
  const [zipCode, setZipCode] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    if (zipCode.length !== 5 || isNaN(zipCode)) {
      setError('Please enter a valid 5-digit zip code.');
      return;
    }
    navigate('/dashboard', { state: { zipCode } });
  };

  return (
    <Container 
      maxWidth="xl"
      sx={{
        height: '100vh',
        backgroundColor: '#003366', // Dark blue background
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      <Box 
        sx={{
          width: { xs: '90%', sm: '400px' },
          backgroundColor: '#f8f8f8', // Off-white background
          border: '2px solid #FF6600', // Orange outline
          borderRadius: 2,
          p: 4,
          textAlign: 'center',
        }}
      >
        <Typography variant="h4" gutterBottom sx={{ color: '#003366', fontWeight: 600 }}>
          HCA Emergency Contact System
        </Typography>
        <Typography variant="subtitle1" gutterBottom sx={{ color: '#003366' }}>
          Enter your zipcode to view affected facilities
        </Typography>
        <Box component="form" onSubmit={handleSubmit} sx={{ mt: 3 }}>
          <TextField
            variant="outlined"
            fullWidth
            label="Zip Code"
            value={zipCode}
            onChange={(e) => {
              setZipCode(e.target.value);
              setError('');
            }}
            inputProps={{ maxLength: 5 }}
            error={!!error}
            helperText={error}
            sx={{ backgroundColor: '#ffffff', borderRadius: 1, mb: 2 }}
          />
          <Button type="submit" fullWidth variant="contained" color="secondary">
            Submit
          </Button>
        </Box>
      </Box>
    </Container>
  );
}

export default ZipCodeEntry;
