// frontend/src/components/ZipCodeEntry.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Container, TextField, Button, Typography, Box } from '@mui/material';

function ZipCodeEntry() {
  const [zipCode, setZipCode] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    if (zipCode.length !== 5 || isNaN(zipCode)) {
      setError('Please enter a valid 5-digit zip code.');
      return;
    }
    navigate('/dashboard', { state: { zipCode } });
  };

  return (
    <Container
      maxWidth="sm"
      sx={{
        height: '100vh',
        backgroundColor: '#003366', // HCA blue
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        textAlign: 'center',
        color: '#ffffff', // white text
        p: 2,
      }}
    >
      <Typography variant="h4" gutterBottom sx={{ fontWeight: 600 }}>
        HCA Emergency Contact System
      </Typography>
      <Typography variant="subtitle1" gutterBottom>
        Enter your zipcode to view affected facilities
      </Typography>
      <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 3, width: '100%' }}>
        <TextField
          variant="outlined"
          fullWidth
          label="Zip Code"
          value={zipCode}
          onChange={(e) => {
            setZipCode(e.target.value);
            setError('');
          }}
          inputProps={{ maxLength: 5 }}
          error={!!error}
          helperText={error}
          sx={{ backgroundColor: '#ffffff', borderRadius: 1, mb: 2 }}
        />
        <Button type="submit" fullWidth variant="contained" color="secondary">
          Submit
        </Button>
      </Box>
    </Container>
  );
}

export default ZipCodeEntry;
