import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { Container, Typography, Box } from '@mui/material';
import MapComponent from './MapComponent';
import PeopleList from './PeopleList';
import EmailModal from './EmailModal';

function DataDashboard() {
  const location = useLocation();
  const zipCode = location.state?.zipCode || '';
  const [data, setData] = useState([]);
  const [selectedEmailData, setSelectedEmailData] = useState(null);

  useEffect(() => {
    if (zipCode) {
      fetch(`http://localhost:8000/api/data/?zip_code=${zipCode}`)
        .then((res) => res.json())
        .then((data) => setData(data))
        .catch((err) => console.error(err));
    }
  }, [zipCode]);

  const openEmailModal = (recipient, subjectType) => {
    let subject = subjectType === 'A' ? 'Subject for Button A' : 'Subject for Button B';
    setSelectedEmailData({ recipient, subject });
  };

  const closeEmailModal = () => {
    setSelectedEmailData(null);
  };

  return (
    <Container sx={{ mt: 4 }}>
      <Typography variant="h5" gutterBottom>
        Data Dashboard for Zip: {zipCode}
      </Typography>
      <Box sx={{ my: 4 }}>
        <MapComponent data={data} />
      </Box>
      <PeopleList people={data} onContact={openEmailModal} />
      {selectedEmailData && (
        <EmailModal emailData={selectedEmailData} onClose={closeEmailModal} />
      )}
    </Container>
  );
}

export default DataDashboard;
