// frontend/src/components/ZipCodeEntry.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Container, Box, Typography, TextField, Button } from '@mui/material';

function ZipCodeEntry() {
  const [zipCode, setZipCode] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    if (zipCode.length !== 5 || isNaN(zipCode)) {
      setError('Please enter a valid 5-digit zip code.');
      return;
    }
    navigate('/dashboard', { state: { zipCode } });
  };

  return (
    <Container 
      maxWidth="xl"
      sx={{
        height: '100vh',
        backgroundColor: '#003366', // Dark blue background
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        position: 'relative',
        // Set a repeated background image using the logo.
        backgroundImage: 'url("/images/HCALOGO-removed")',
        backgroundRepeat: 'repeat',
        backgroundSize: '150px', // Adjust size for spacing
        backgroundPosition: 'center',
        opacity: 0.9,
      }}
    >
      <Box 
        sx={{
          width: { xs: '90%', sm: '500px' },
          backgroundColor: '#f8f8f8', // Off-white background for entry box
          border: '2px solid #FF6600', // Orange outline
          borderRadius: 2,
          p: 4,
          textAlign: 'center',
          position: 'relative',
          zIndex: 1,
        }}
      >
        <Typography variant="h4" gutterBottom sx={{ color: '#003366', fontWeight: 600 }}>
          HCA Emergency Contact System
        </Typography>
        <Typography variant="subtitle1" gutterBottom sx={{ color: '#003366' }}>
          Enter your zipcode to view affected facilities
        </Typography>
        <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 3 }}>
          <TextField
            variant="outlined"
            fullWidth
            label="Zip Code"
            value={zipCode}
            onChange={(e) => {
              setZipCode(e.target.value);
              setError('');
            }}
            inputProps={{ maxLength: 5 }}
            error={!!error}
            helperText={error}
            InputProps={{
              sx: { backgroundColor: '#ffffff' },
            }}
            InputLabelProps={{
              sx: {
                color: '#003366',
                '&.Mui-focused': { color: '#003366' },
              },
            }}
            sx={{
              mb: 2,
              '& .MuiOutlinedInput-root': {
                '& fieldset': {
                  borderColor: '#003366',
                },
                '&:hover fieldset': {
                  borderColor: '#003366',
                },
                '&.Mui-focused fieldset': {
                  borderColor: '#003366',
                },
                '& input': {
                  color: '#000000',
                },
              },
            }}
          />
          <Button type="submit" fullWidth variant="contained" color="secondary" sx={{ mt: 2 }}>
            Submit
          </Button>
        </Box>
      </Box>
    </Container>
  );
}

export default ZipCodeEntry;
