// frontend/src/components/ZipCodeEntry.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Container, Box, Typography, TextField, Button } from '@mui/material';

function ZipCodeEntry() {
  const [zipCode, setZipCode] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    if (zipCode.length !== 5 || isNaN(zipCode)) {
      setError('Please enter a valid 5-digit zip code.');
      return;
    }
    navigate('/dashboard', { state: { zipCode } });
  };

  return (
    <Container 
      maxWidth="xl"
      sx={{
        height: '100vh',
        backgroundColor: '#003366', // Dark blue background
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        position: 'relative',
        '&::before': {
          content: '""',
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          backgroundImage: 'url("https://www.hcahealthcare.com/wp-content/uploads/2016/03/HCA_Logo.png")',
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
          backgroundSize: '200px',
          opacity: 0.1,
          zIndex: 0,
        },
      }}
    >
      <Box 
        sx={{
          width: { xs: '90%', sm: '500px' },
          backgroundColor: '#f8f8f8', // Off-white background
          border: '2px solid #FF6600', // Orange outline for container
          borderRadius: 2,
          p: 4,
          textAlign: 'center',
          position: 'relative', // Place above the pseudo-element
          zIndex: 1,
        }}
      >
        <Typography variant="h4" gutterBottom sx={{ color: '#003366', fontWeight: 600 }}>
          HCA Emergency Contact System
        </Typography>
        <Typography variant="subtitle1" gutterBottom sx={{ color: '#003366' }}>
          Enter your zipcode to view affected facilities
        </Typography>
        <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 3 }}>
          <TextField
            variant="outlined"
            fullWidth
            label="Zip Code"
            value={zipCode}
            onChange={(e) => {
              setZipCode(e.target.value);
              setError('');
            }}
            inputProps={{ maxLength: 5 }}
            error={!!error}
            helperText={error}
            InputProps={{
              sx: {
                backgroundColor: '#ffffff', // Keep input background white
              },
            }}
            InputLabelProps={{
              sx: {
                color: '#003366', // Label color dark blue
                '&.Mui-focused': {
                  color: '#003366',
                },
              },
            }}
            sx={{
              mb: 2,
              '& .MuiOutlinedInput-root': {
                '& fieldset': {
                  borderColor: '#003366',
                },
                '&:hover fieldset': {
                  borderColor: '#003366',
                },
                '&.Mui-focused fieldset': {
                  borderColor: '#003366',
                },
                '& input': {
                  color: '#000000', // Input text color black
                },
              },
            }}
          />
          <Button type="submit" fullWidth variant="contained" color="secondary" sx={{ mt: 2 }}>
            Submit
          </Button>
        </Box>
      </Box>
    </Container>
  );
}

export default ZipCodeEntry;
