import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import MapComponent from './MapComponent';
import PeopleList from './PeopleList';
import EmailModal from './EmailModal';

function DataDashboard() {
  const location = useLocation();
  const zipCode = location.state?.zipCode || '';
  const [data, setData] = useState([]);
  const [selectedEmailData, setSelectedEmailData] = useState(null);

  useEffect(() => {
    if (zipCode) {
      fetch(`http://localhost:8000/api/data/?zip_code=${zipCode}`)
        .then((res) => res.json())
        .then((data) => setData(data))
        .catch((err) => console.error(err));
    }
  }, [zipCode]);

  const openEmailModal = (recipient, subjectType) => {
    let subject = '';
    if (subjectType === 'A') {
      subject = 'Subject for Button A';
    } else if (subjectType === 'B') {
      subject = 'Subject for Button B';
    }
    setSelectedEmailData({ recipient, subject });
  };

  const closeEmailModal = () => {
    setSelectedEmailData(null);
  };

  return (
    <div className="container">
      <h1>Data Dashboard for Zip: {zipCode}</h1>
      <MapComponent data={data} />
      <PeopleList people={data} onContact={openEmailModal} />
      {selectedEmailData && (
        <EmailModal 
          emailData={selectedEmailData} 
          onClose={closeEmailModal} 
        />
      )}
    </div>
  );
}

export default DataDashboard;
