// frontend/src/components/PeopleList.js

import React from 'react';
import { 
  Table, TableBody, TableCell, TableContainer, 
  TableHead, TableRow, Paper, Button, Typography 
} from '@mui/material';

function PeopleList({ people, onContact }) {
  return (
    <div>
      <Typography variant="h6" gutterBottom sx={{ color: '#003366' }}>
        Facility List
      </Typography>
      {people.length === 0 ? (
        <Typography sx={{ color: '#003366' }}>No facilities found for this group.</Typography>
      ) : (
        <TableContainer component={Paper} sx={{ mt: 2, backgroundColor: '#f8f8f8' }}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell sx={{ color: '#003366' }}>Name</TableCell>
                <TableCell sx={{ color: '#003366' }}>Location</TableCell>
                <TableCell sx={{ color: '#003366' }}>City</TableCell>
                <TableCell sx={{ color: '#003366' }}>State</TableCell>
                <TableCell sx={{ color: '#003366' }}>Department</TableCell>
                <TableCell sx={{ color: '#003366' }}>Position</TableCell>
                <TableCell sx={{ color: '#003366' }}>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {people.map((person) => (
                <TableRow key={person.id}>
                  <TableCell sx={{ color: '#003366' }}>
                    {person.first_name} {person.last_name}
                  </TableCell>
                  <TableCell sx={{ color: '#003366' }}>
                    {person.location}
                  </TableCell>
                  <TableCell sx={{ color: '#003366' }}>
                    {person.city}
                  </TableCell>
                  <TableCell sx={{ color: '#003366' }}>
                    {person.state}
                  </TableCell>
                  <TableCell sx={{ color: '#003366' }}>
                    {person.department}
                  </TableCell>
                  <TableCell sx={{ color: '#003366' }}>
                    {person.position}
                  </TableCell>
                  <TableCell>
                    <Button 
                      variant="outlined" 
                      onClick={() => onContact(1)}
                      sx={{ color: '#FF6600', borderColor: '#FF6600', mr: 1 }}
                    >
                      Contact
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      )}
    </div>
  );
}

export default PeopleList;
