// frontend/src/components/LandingChoice.js

import React from 'react';
import { useNavigate } from 'react-router-dom';
import { Container, Box, Typography, Grid, Paper } from '@mui/material';

// Reuse the LogoBackground component from ZipCodeEntry.
// If it's not exported, you can copy it here.
function LogoBackground() {
  // Fixed positions for logos; adjust as needed.
  const positions = [
    { top: '10%', left: '20%' },
    { top: '10%', left: '80%' },
    { top: '30%', left: '50%' },
    { top: '50%', left: '20%' },
    { top: '50%', left: '80%' },
    { top: '70%', left: '40%' },
    { top: '70%', left: '70%' },
    { top: '90%', left: '30%' },
    { top: '90%', left: '70%' },
  ];

  return (
    <>
      {positions.map((pos, index) => (
        <img
          key={index}
          src="/images/hca_logo.png" // Ensure the logo exists in public/images/
          alt="HCA Logo"
          style={{
            position: 'absolute',
            top: pos.top,
            left: pos.left,
            width: '120px',
            opacity: 0.5,
            transform: 'translate(-50%, -50%)',
            zIndex: 0,
          }}
        />
      ))}
    </>
  );
}

function LandingChoice() {
  const navigate = useNavigate();

  const handleActClick = () => {
    navigate('/zipcode-entry');
  };

  const handlePrepareClick = () => {
    navigate('/prepare'); // Update this route as needed
  };

  return (
    <Container
      maxWidth="xl"
      sx={{
        height: '100vh',
        width: '100%',
        backgroundColor: '#003366', // Dark blue background
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        position: 'relative',
      }}
    >
      {/* Render fixed background logos */}
      <LogoBackground />

      {/* Main centered box */}
      <Box
        sx={{
          width: { xs: '90%', sm: '600px' },
          backgroundColor: '#f8f8f8', // Off-white
          border: '2px solid #FF6600', // Orange outline
          borderRadius: 2,
          p: 4,
          textAlign: 'center',
          position: 'relative',
          zIndex: 1,
        }}
      >
        <Typography variant="h4" sx={{ color: '#ffffff', backgroundColor: 'transparent', mb: 3, fontWeight: 600 }}>
          HCA Emergency Response Resource
        </Typography>
        {/* Two clickable boxes side by side */}
        <Grid container spacing={2} justifyContent="center">
          <Grid item xs={12} sm={6}>
            <Paper
              onClick={handlePrepareClick}
              sx={{
                height: 200,
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                backgroundColor: '#f8f8f8',
                border: '2px solid #FF6600',
                cursor: 'pointer',
              }}
              elevation={3}
            >
              <Typography variant="h5" sx={{ color: '#003366', fontWeight: 600 }}>
                Prepare
              </Typography>
            </Paper>
          </Grid>
          <Grid item xs={12} sm={6}>
            <Paper
              onClick={handleActClick}
              sx={{
                height: 200,
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                backgroundColor: '#f8f8f8',
                border: '2px solid #FF6600',
                cursor: 'pointer',
              }}
              elevation={3}
            >
              <Typography variant="h5" sx={{ color: '#003366', fontWeight: 600 }}>
                Act
              </Typography>
            </Paper>
          </Grid>
        </Grid>
      </Box>
    </Container>
  );
}

export default LandingChoice;
