import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { Container, Typography, Box, Slider } from '@mui/material';
import MapComponent from './MapComponent';
import PeopleList from './PeopleList';
import EmailModal from './EmailModal';

function DataDashboard() {
  // Retrieve the zip code (passed from a previous page, e.g., via state)
  const location = useLocation();
  const zipCode = location.state?.zipCode || '';
  console.log("Zip code from DataDashboard:", zipCode);

  // Two state variables to store local and nearby data
  const [localData, setLocalData] = useState([]);
  const [nearbyData, setNearbyData] = useState([]);

  // State for the slider (radius, defaulting to 50)
  const [radius, setRadius] = useState(50);

  // State for handling the email modal (if needed)
  const [selectedEmailData, setSelectedEmailData] = useState(null);

  // Handler for slider change
  const handleRadiusChange = (event, newValue) => {
    setRadius(newValue);
  };

  // Fetch data whenever zipCode or radius changes
  useEffect(() => {
    if (zipCode) {
      // Include the radius in your query parameters
      fetch(`http://localhost:8000/api/data/?zip_code=${zipCode}&radius=${radius}`)
        .then((res) => res.json())
        .then((data) => {
          console.log("API response:", data);
          setLocalData(data.local);
          setNearbyData(data.nearby);
        })
        .catch((err) => console.error(err));
    }
  }, [zipCode, radius]);

  const openEmailModal = (recipient, subjectType) => {
    const subject = subjectType === 'A' ? 'Subject for Button A' : 'Subject for Button B';
    setSelectedEmailData({ recipient, subject });
  };

  const closeEmailModal = () => {
    setSelectedEmailData(null);
  };

  return (
    <Container sx={{ mt: 4 }}>
      <Typography variant="h5" gutterBottom>
        Data Dashboard for Zip: {zipCode}
      </Typography>

      {/* Radius slider */}
      <Box sx={{ my: 2 }}>
        <Typography gutterBottom>
          Select Nearby Radius (miles): {radius}
        </Typography>
        <Slider
          value={radius}
          onChange={handleRadiusChange}
          aria-labelledby="radius-slider"
          valueLabelDisplay="auto"
          min={0}
          max={100}
        />
      </Box>

      {/* Map view: Display markers for both local and nearby people */}
      <Box sx={{ my: 4 }}>
        <MapComponent data={[...localData, ...nearbyData]} />
      </Box>

      {/* List view for local data */}
      <Typography variant="h6" gutterBottom>
        People in Your Zip Code
      </Typography>
      <PeopleList people={localData} onContact={openEmailModal} />

      {/* List view for nearby data */}
      <Typography variant="h6" gutterBottom sx={{ mt: 4 }}>
        People Nearby (within {radius} miles)
      </Typography>
      <PeopleList people={nearbyData} onContact={openEmailModal} />

      {selectedEmailData && (
        <EmailModal emailData={selectedEmailData} onClose={closeEmailModal} />
      )}
    </Container>
  );
}

export default DataDashboard;
