// frontend/src/components/EmailModal.js

import React, { useState } from 'react';
import { Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField, Typography } from '@mui/material';

function EmailModal({ emailData, onClose }) {
  const [message, setMessage] = useState('');

  const handleSend = () => {
    const recipients = emailData.recipients;
    const count = Array.isArray(recipients) ? recipients.length : (recipients ? 1 : 0);
    alert(`Email sent to ${count} people.`);
    onClose();
  };

  return (
    <Dialog open onClose={onClose}>
      <DialogTitle>Send Email</DialogTitle>
      <DialogContent>
        <Typography variant="body1" gutterBottom>
          {`Sending email to ${Array.isArray(emailData.recipients) ? emailData.recipients.length : 1} people.`}
        </Typography>
        <TextField
          autoFocus
          margin="dense"
          label="Subject"
          fullWidth
          variant="outlined"
          value={emailData.subject}
          disabled
        />
        <TextField
          margin="dense"
          label="Message"
          fullWidth
          multiline
          rows={4}
          variant="outlined"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Cancel</Button>
        <Button onClick={handleSend} variant="contained" color="primary">
          Send Email
        </Button>
      </DialogActions>
    </Dialog>
  );
}

export default EmailModal;
