sourcesContent:
import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import {
  Container,
  Box,
  Typography,
  Grid,
  Paper,
  Card,
  CardContent,
  Divider,
  Chip,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  CircularProgress,
  Button,
  IconButton,
  Tooltip,
  Tab,
  Tabs,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
} from "@mui/material";
import HomeButton from "./HomeButton";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip as RechartsTooltip,
  Legend,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
  LineChart,
  Line,
} from "recharts";
import useDebounce from "../hooks/useDebounce";

// Simulated data for dashboard
const facilityData = [
  {
    name: "Tampa General Hospital",
    state: "FL",
    hazardLevel: "High",
    readinessScore: 85,
    bedCapacity: 1011,
    emergencyStaff: 287,
  },
  {
    name: "Memorial Hermann Hospital",
    state: "TX",
    hazardLevel: "Very High",
    readinessScore: 92,
    bedCapacity: 657,
    emergencyStaff: 198,
  },
  {
    name: "Orlando Regional Medical Center",
    state: "FL",
    hazardLevel: "High",
    readinessScore: 79,
    bedCapacity: 808,
    emergencyStaff: 235,
  },
  {
    name: "Methodist Hospital",
    state: "TX",
    hazardLevel: "Very High",
    readinessScore: 81,
    bedCapacity: 573,
    emergencyStaff: 178,
  },
  {
    name: "North Florida Regional Medical Center",
    state: "FL",
    hazardLevel: "Extreme",
    readinessScore: 88,
    bedCapacity: 432,
    emergencyStaff: 156,
  },
  {
    name: "West Florida Hospital",
    state: "FL",
    hazardLevel: "High",
    readinessScore: 77,
    bedCapacity: 531,
    emergencyStaff: 167,
  },
  {
    name: "Bayshore Medical Center",
    state: "TX",
    hazardLevel: "High",
    readinessScore: 75,
    bedCapacity: 364,
    emergencyStaff: 122,
  },
  {
    name: "Doctors Hospital of Sarasota",
    state: "FL",
    hazardLevel: "Moderate",
    readinessScore: 82,
    bedCapacity: 155,
    emergencyStaff: 87,
  },
  {
    name: "Corpus Christi Medical Center",
    state: "TX",
    hazardLevel: "Very High",
    readinessScore: 84,
    bedCapacity: 413,
    emergencyStaff: 135,
  },
  {
    name: "Kendall Regional Medical Center",
    state: "FL",
    hazardLevel: "High",
    readinessScore: 80,
    bedCapacity: 417,
    emergencyStaff: 143,
  },
];

const hazardDistribution = [
  { name: "Extreme", value: 1 },
  { name: "Very High", value: 3 },
  { name: "High", value: 5 },
  { name: "Moderate", value: 1 },
  { name: "Low", value: 0 },
];

const stateDistribution = [
  { name: "Florida", value: 6 },
  { name: "Texas", value: 4 },
  { name: "Georgia", value: 0 },
  { name: "Louisiana", value: 0 },
  { name: "Other", value: 0 },
];

const monthlyIncidents = [
  { month: "Jan", incidents: 12 },
  { month: "Feb", incidents: 9 },
  { month: "Mar", incidents: 15 },
  { month: "Apr", incidents: 23 },
  { month: "May", incidents: 28 },
  { month: "Jun", incidents: 37 },
  { month: "Jul", incidents: 42 },
  { month: "Aug", incidents: 58 },
  { month: "Sep", incidents: 61 },
  { month: "Oct", incidents: 47 },
  { month: "Nov", incidents: 31 },
  { month: "Dec", incidents: 19 },
];

const resourceDistribution = [
  { name: "Beds Available", value: 4361 },
  { name: "Beds Occupied", value: 1500 },
];

// Color constants matching the app's theme
const COLORS = ["#FF6600", "#FF8533", "#FFA366", "#FFBF99", "#FFD9CC"];
const BLUE_COLORS = ["#003366", "#004080", "#004d99", "#0059b3", "#0066cc"];

function LocalDashboard() {
  const location = useLocation();
  const defaultZipCode = "33612"; // Default to a Florida zip code for HCA hospitals
  const zipCode = location.state?.zipCode || defaultZipCode;

  const [tabValue, setTabValue] = useState(0);
  const [localData, setLocalData] = useState([]);
  const [nearbyData, setNearbyData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [radius, setRadius] = useState(100); // Default 100 mile radius to capture more data
  const debouncedRadius = useDebounce(radius, 500);

  // Derived state for dashboard data
  const [facilityData, setFacilityData] = useState([]);
  const [stateDistribution, setStateDistribution] = useState([]);
  const [totalBeds, setTotalBeds] = useState(0);
  const [totalStaff, setTotalStaff] = useState(0);

  // Monthly incidents data - no real API for this, keeping simulated
  const monthlyIncidents = [
    { month: "Jan", incidents: 12 },
    { month: "Feb", incidents: 9 },
    { month: "Mar", incidents: 15 },
    { month: "Apr", incidents: 23 },
    { month: "May", incidents: 28 },
    { month: "Jun", incidents: 37 },
    { month: "Jul", incidents: 42 },
    { month: "Aug", incidents: 58 },
    { month: "Sep", incidents: 61 },
    { month: "Oct", incidents: 47 },
    { month: "Nov", incidents: 31 },
    { month: "Dec", incidents: 19 },
  ];

  // Hazard distribution - no real API for this, keeping simulated
  const hazardDistribution = [
    { name: "Extreme", value: 1 },
    { name: "Very High", value: 3 },
    { name: "High", value: 5 },
    { name: "Moderate", value: 1 },
    { name: "Low", value: 0 },
  ];

  // Resource distribution - will update based on real data
  const [resourceDistribution, setResourceDistribution] = useState([
    { name: "Beds Available", value: 4361 },
    { name: "Beds Occupied", value: 1500 },
  ]);

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  // Fetch data from the API
  useEffect(() => {
    if (zipCode) {
      setLoading(true);
      fetch(
        `http://localhost:8000/api/data/?zip_code=${zipCode}&radius=${debouncedRadius}`
      )
        .then((res) => res.json())
        .then((data) => {
          console.log("API response for dashboard:", data);
          setLocalData(data.local);
          setNearbyData(data.nearby);
          processData([...data.local, ...data.nearby]);
          setLoading(false);
        })
        .catch((err) => {
          console.error("Error fetching data for dashboard:", err);
          setLoading(false);
        });
    }
  }, [zipCode, debouncedRadius]);

  // Process API data to create dashboard data
  const processData = (combinedData) => {
    if (!combinedData || combinedData.length === 0) return;

    // Process facility data
    const facilities = processFacilityData(combinedData);
    setFacilityData(facilities);

    // Process state distribution
    const states = processStateDistribution(facilities);
    setStateDistribution(states);

    // Calculate totals
    const beds = facilities.reduce(
      (sum, facility) => sum + (facility.bedCapacity || 0),
      0
    );
    setTotalBeds(beds);

    const staff = facilities.reduce(
      (sum, facility) => sum + (facility.emergencyStaff || 0),
      0
    );
    setTotalStaff(staff);

    // Update resource distribution
    setResourceDistribution([
      { name: "Beds Available", value: Math.round(beds * 0.74) }, // Assuming 74% availability
      { name: "Beds Occupied", value: Math.round(beds * 0.26) },
    ]);
  };

  // Group data by location to get facility-level data
  const processFacilityData = (data) => {
    const locationMap = new Map();

    // Group by location
    data.forEach((person) => {
      if (!locationMap.has(person.location)) {
        locationMap.set(person.location, {
          name: person.location,
          state: person.state,
          city: person.city,
          zip_code: person.zip_code,
          latitude: person.latitude,
          longitude: person.longitude,
          staff: [],
        });
      }

      locationMap.get(person.location).staff.push(person);
    });

    // Convert the map to an array and calculate derived properties
    return Array.from(locationMap.values()).map((facility) => {
      // Count medical staff
      const medicalStaff = facility.staff.filter(
        (person) => person.is_medical === "Medical"
      ).length;

      // Assign a hazard level based on state (simulated)
      const hazardLevel =
        facility.state === "FL"
          ? Math.random() > 0.5
            ? "High"
            : "Very High"
          : facility.state === "TX"
          ? Math.random() > 0.7
            ? "Very High"
            : "High"
          : facility.state === "LA"
          ? "Extreme"
          : facility.state === "GA"
          ? "Moderate"
          : "Low";

      // Assign a readiness score (simulated, but based on staff count)
      const readinessScore = Math.max(70, Math.min(95, 75 + medicalStaff / 10));

      // Estimate bed capacity based on staff (simulated)
      const bedCapacity = Math.round(medicalStaff * 3.5);

      return {
        name: facility.name,
        state: facility.state,
        city: facility.city,
        zip_code: facility.zip_code,
        latitude: facility.latitude,
        longitude: facility.longitude,
        hazardLevel,
        readinessScore: Math.round(readinessScore),
        bedCapacity,
        emergencyStaff: medicalStaff,
        totalStaff: facility.staff.length,
      };
    });
  };

  // Count facilities by state
  const processStateDistribution = (facilities) => {
    const stateCounts = {};

    facilities.forEach((facility) => {
      stateCounts[facility.state] = (stateCounts[facility.state] || 0) + 1;
    });

    // Convert to the required format for the pie chart
    return Object.entries(stateCounts)
      .map(([state, count]) => ({
        name: getStateName(state),
        value: count,
      }))
      .sort((a, b) => b.value - a.value);
  };

  // Convert state abbreviation to full name
  const getStateName = (stateCode) => {
    const states = {
      AL: "Alabama",
      AK: "Alaska",
      AZ: "Arizona",
      AR: "Arkansas",
      CA: "California",
      CO: "Colorado",
      CT: "Connecticut",
      DE: "Delaware",
      FL: "Florida",
      GA: "Georgia",
      HI: "Hawaii",
      ID: "Idaho",
      IL: "Illinois",
      IN: "Indiana",
      IA: "Iowa",
      KS: "Kansas",
      KY: "Kentucky",
      LA: "Louisiana",
      ME: "Maine",
      MD: "Maryland",
      MA: "Massachusetts",
      MI: "Michigan",
      MN: "Minnesota",
      MS: "Mississippi",
      MO: "Missouri",
      MT: "Montana",
      NE: "Nebraska",
      NV: "Nevada",
      NH: "New Hampshire",
      NJ: "New Jersey",
      NM: "New Mexico",
      NY: "New York",
      NC: "North Carolina",
      ND: "North Dakota",
      OH: "Ohio",
      OK: "Oklahoma",
      OR: "Oregon",
      PA: "Pennsylvania",
      RI: "Rhode Island",
      SC: "South Carolina",
      SD: "South Dakota",
      TN: "Tennessee",
      TX: "Texas",
      UT: "Utah",
      VT: "Vermont",
      VA: "Virginia",
      WA: "Washington",
      WV: "West Virginia",
      WI: "Wisconsin",
      WY: "Wyoming",
    };
    return states[stateCode] || stateCode;
  };

  return (
    <Container
      maxWidth="xl"
      sx={{
        width: "100%",
        minHeight: "100vh",
        backgroundColor: "#003366", // Dark blue background
        pt: 4,
        pb: 4,
        position: "relative",
      }}
    >
      <HomeButton />
      <Box sx={{ textAlign: "center", mb: 4 }}>
        <Typography variant="h4" sx={{ color: "#ffffff", fontWeight: 600 }}>
          Disaster Preparedness Dashboard
        </Typography>
        <Typography variant="subtitle1" sx={{ color: "#f8f8f8", mb: 2 }}>
          Real-time monitoring and status of HCA facilities in high-risk areas
        </Typography>
        {zipCode && (
          <Typography variant="subtitle2" sx={{ color: "#f8f8f8" }}>
            Showing data for zip code: {zipCode}
          </Typography>
        )}
      </Box>

      {/* Loading indicator */}
      {loading && (
        <Box sx={{ display: "flex", justifyContent: "center", my: 4 }}>
          <CircularProgress sx={{ color: "#FF6600" }} />
        </Box>
      )}

      {!loading && (
        <>
          {/* Dashboard Tabs */}
          <Box sx={{ mb: 3 }}>
            <Tabs
              value={tabValue}
              onChange={handleTabChange}
              centered
              sx={{
                "& .MuiTab-root": { color: "#f8f8f8" },
                "& .Mui-selected": { color: "#FF6600" },
                "& .MuiTabs-indicator": { backgroundColor: "#FF6600" },
              }}
            >
              <Tab label="Overview" />
              <Tab label="Facility Status" />
              <Tab label="Resource Allocation" />
            </Tabs>
          </Box>

          {/* Tab 1: Overview */}
          {tabValue === 0 && (
            <Grid container spacing={3}>
              {/* Key Statistics Cards */}
              <Grid item xs={12}>
                <Grid container spacing={3}>
                  <Grid item xs={12} sm={6} md={3}>
                    <Card sx={{ backgroundColor: "#004080", color: "#ffffff" }}>
                      <CardContent sx={{ textAlign: "center" }}>
                        <Typography
                          variant="h6"
                          gutterBottom
                          sx={{ color: "#ffffff" }}
                        >
                          Total Facilities
                        </Typography>
                        <Typography
                          variant="h3"
                          sx={{ color: "#FF6600", fontWeight: "bold" }}
                        >
                          {facilityData.length || 0}
                        </Typography>
                        <Typography variant="body2" sx={{ color: "#f8f8f8" }}>
                          in high-risk areas
                        </Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                  <Grid item xs={12} sm={6} md={3}>
                    <Card sx={{ backgroundColor: "#004080", color: "#ffffff" }}>
                      <CardContent sx={{ textAlign: "center" }}>
                        <Typography
                          variant="h6"
                          gutterBottom
                          sx={{ color: "#ffffff" }}
                        >
                          Average Readiness
                        </Typography>
                        <Typography
                          variant="h3"
                          sx={{ color: "#FF6600", fontWeight: "bold" }}
                        >
                          {facilityData.length
                            ? Math.round(
                                facilityData.reduce(
                                  (sum, facility) =>
                                    sum + facility.readinessScore,
                                  0
                                ) / facilityData.length
                              )
                            : 0}
                          %
                        </Typography>
                        <Typography variant="body2" sx={{ color: "#f8f8f8" }}>
                          across all facilities
                        </Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                  <Grid item xs={12} sm={6} md={3}>
                    <Card sx={{ backgroundColor: "#004080", color: "#ffffff" }}>
                      <CardContent sx={{ textAlign: "center" }}>
                        <Typography
                          variant="h6"
                          gutterBottom
                          sx={{ color: "#ffffff" }}
                        >
                          Total Bed Capacity
                        </Typography>
                        <Typography
                          variant="h3"
                          sx={{ color: "#FF6600", fontWeight: "bold" }}
                        >
                          {totalBeds.toLocaleString()}
                        </Typography>
                        <Typography variant="body2" sx={{ color: "#f8f8f8" }}>
                          72% currently available
                        </Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                  <Grid item xs={12} sm={6} md={3}>
                    <Card sx={{ backgroundColor: "#004080", color: "#ffffff" }}>
                      <CardContent sx={{ textAlign: "center" }}>
                        <Typography
                          variant="h6"
                          gutterBottom
                          sx={{ color: "#ffffff" }}
                        >
                          Emergency Staff
                        </Typography>
                        <Typography
                          variant="h3"
                          sx={{ color: "#FF6600", fontWeight: "bold" }}
                        >
                          {totalStaff.toLocaleString()}
                        </Typography>
                        <Typography variant="body2" sx={{ color: "#f8f8f8" }}>
                          trained for crisis response
                        </Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                </Grid>
              </Grid>

              {/* Yearly Incident Trend */}
              <Grid item xs={12} md={8}>
                <Paper
                  sx={{
                    p: 3,
                    backgroundColor: "rgba(255, 255, 255, 0.9)",
                    height: "100%",
                  }}
                >
                  <Typography
                    variant="h6"
                    gutterBottom
                    sx={{ color: "#003366" }}
                  >
                    Hurricane & Severe Weather Incidents (2023)
                  </Typography>
                  <ResponsiveContainer width="100%" height={300}>
                    <LineChart
                      data={monthlyIncidents}
                      margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
                    >
                      <CartesianGrid strokeDasharray="3 3" stroke="#ccc" />
                      <XAxis dataKey="month" stroke="#003366" />
                      <YAxis stroke="#003366" />
                      <RechartsTooltip
                        contentStyle={{
                          backgroundColor: "rgba(255, 255, 255, 0.95)",
                          color: "#003366",
                        }}
                      />
                      <Line
                        type="monotone"
                        dataKey="incidents"
                        stroke="#FF6600"
                        strokeWidth={3}
                        dot={{ stroke: "#FF6600", strokeWidth: 2, r: 4 }}
                        activeDot={{ r: 8 }}
                      />
                    </LineChart>
                  </ResponsiveContainer>
                  <Typography variant="body2" sx={{ color: "#003366", mt: 2 }}>
                    Peak season observed from August to October with 166 total
                    incidents recorded in 2023
                  </Typography>
                </Paper>
              </Grid>

              {/* Distribution Charts */}
              <Grid item xs={12} md={4}>
                <Paper
                  sx={{
                    p: 3,
                    backgroundColor: "rgba(255, 255, 255, 0.9)",
                    height: "100%",
                    display: "flex",
                    flexDirection: "column",
                  }}
                >
                  <Typography
                    variant="h6"
                    gutterBottom
                    sx={{ color: "#003366" }}
                  >
                    Facilities by Hazard Level
                  </Typography>
                  <Box
                    sx={{ flex: 1, display: "flex", justifyContent: "center" }}
                  >
                    <ResponsiveContainer width="100%" height={300}>
                      <PieChart>
                        <Pie
                          data={hazardDistribution}
                          cx="50%"
                          cy="50%"
                          labelLine={false}
                          label={({ name, percent }) =>
                            `${name}: ${(percent * 100).toFixed(0)}%`
                          }
                          outerRadius={80}
                          fill="#8884d8"
                          dataKey="value"
                        >
                          {hazardDistribution.map((entry, index) => (
                            <Cell
                              key={`cell-${index}`}
                              fill={COLORS[index % COLORS.length]}
                            />
                          ))}
                        </Pie>
                        <RechartsTooltip
                          contentStyle={{
                            backgroundColor: "rgba(255, 255, 255, 0.95)",
                            color: "#003366",
                          }}
                        />
                      </PieChart>
                    </ResponsiveContainer>
                  </Box>
                  <Divider sx={{ my: 1 }} />
                  <Box sx={{ mt: 1 }}>
                    <Typography variant="body2" sx={{ color: "#003366" }}>
                      40% of facilities are in High or Very High hazard zones,
                      requiring prioritized resource allocation.
                    </Typography>
                  </Box>
                </Paper>
              </Grid>

              {/* Alert Status */}
              <Grid item xs={12}>
                <Paper
                  sx={{
                    p: 3,
                    backgroundColor: "rgba(255, 255, 255, 0.9)",
                  }}
                >
                  <Typography
                    variant="h6"
                    gutterBottom
                    sx={{ color: "#003366" }}
                  >
                    Current Alert Status
                  </Typography>
                  <Grid container spacing={2} sx={{ mt: 1 }}>
                    <Grid item xs={12} sm={4}>
                      <Card sx={{ backgroundColor: "#ffeb3b" }}>
                        <CardContent>
                          <Typography
                            variant="h6"
                            gutterBottom
                            sx={{ color: "#000000" }}
                          >
                            Tropical Storm Watch
                          </Typography>
                          <Typography variant="body2" sx={{ color: "#000000" }}>
                            Affecting: South Florida facilities
                          </Typography>
                          <Typography variant="body2" sx={{ color: "#000000" }}>
                            Issued: Oct 15, 2023 - 08:00 EST
                          </Typography>
                        </CardContent>
                      </Card>
                    </Grid>
                    <Grid item xs={12} sm={4}>
                      <Card sx={{ backgroundColor: "#f44336" }}>
                        <CardContent>
                          <Typography
                            variant="h6"
                            gutterBottom
                            sx={{ color: "#ffffff" }}
                          >
                            Hurricane Warning
                          </Typography>
                          <Typography variant="body2" sx={{ color: "#ffffff" }}>
                            Affecting: Gulf Coast facilities
                          </Typography>
                          <Typography variant="body2" sx={{ color: "#ffffff" }}>
                            Issued: Oct 14, 2023 - 18:30 EST
                          </Typography>
                        </CardContent>
                      </Card>
                    </Grid>
                    <Grid item xs={12} sm={4}>
                      <Card sx={{ backgroundColor: "#4caf50" }}>
                        <CardContent>
                          <Typography
                            variant="h6"
                            gutterBottom
                            sx={{ color: "#ffffff" }}
                          >
                            All Clear
                          </Typography>
                          <Typography variant="body2" sx={{ color: "#ffffff" }}>
                            Affecting: Georgia facilities
                          </Typography>
                          <Typography variant="body2" sx={{ color: "#ffffff" }}>
                            Issued: Oct 13, 2023 - 12:15 EST
                          </Typography>
                        </CardContent>
                      </Card>
                    </Grid>
                  </Grid>
                </Paper>
              </Grid>
            </Grid>
          )}

          {/* Tab 2: Facility Status */}
          {tabValue === 1 && (
            <Grid container spacing={3}>
              <Grid item xs={12}>
                <Paper
                  sx={{
                    p: 3,
                    backgroundColor: "rgba(255, 255, 255, 0.9)",
                  }}
                >
                  <Typography
                    variant="h6"
                    gutterBottom
                    sx={{ color: "#003366" }}
                  >
                    High Risk Facilities
                  </Typography>
                  <TableContainer>
                    <Table aria-label="facility status table">
                      <TableHead>
                        <TableRow>
                          <TableCell
                            sx={{ fontWeight: "bold", color: "#003366" }}
                          >
                            Facility Name
                          </TableCell>
                          <TableCell
                            sx={{ fontWeight: "bold", color: "#003366" }}
                          >
                            State
                          </TableCell>
                          <TableCell
                            sx={{ fontWeight: "bold", color: "#003366" }}
                          >
                            Hazard Level
                          </TableCell>
                          <TableCell
                            sx={{ fontWeight: "bold", color: "#003366" }}
                          >
                            Readiness Score
                          </TableCell>
                          <TableCell
                            sx={{ fontWeight: "bold", color: "#003366" }}
                          >
                            Bed Capacity
                          </TableCell>
                          <TableCell
                            sx={{ fontWeight: "bold", color: "#003366" }}
                          >
                            Emergency Staff
                          </TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {facilityData.map((facility) => (
                          <TableRow key={facility.name}>
                            <TableCell sx={{ color: "#003366" }}>
                              {facility.name}
                            </TableCell>
                            <TableCell sx={{ color: "#003366" }}>
                              {facility.state}
                            </TableCell>
                            <TableCell>
                              <Chip
                                label={facility.hazardLevel}
                                color={
                                  facility.hazardLevel === "Extreme"
                                    ? "error"
                                    : facility.hazardLevel === "Very High"
                                    ? "warning"
                                    : facility.hazardLevel === "High"
                                    ? "primary"
                                    : facility.hazardLevel === "Moderate"
                                    ? "info"
                                    : "success"
                                }
                                sx={{
                                  fontWeight: "bold",
                                  bgcolor:
                                    facility.hazardLevel === "Extreme"
                                      ? "#d32f2f"
                                      : facility.hazardLevel === "Very High"
                                      ? "#ff9800"
                                      : facility.hazardLevel === "High"
                                      ? "#2196f3"
                                      : facility.hazardLevel === "Moderate"
                                      ? "#03a9f4"
                                      : "#4caf50",
                                  color: "#ffffff",
                                }}
                              />
                            </TableCell>
                            <TableCell>
                              <Box
                                sx={{ display: "flex", alignItems: "center" }}
                              >
                                <CircularProgress
                                  variant="determinate"
                                  value={facility.readinessScore}
                                  size={30}
                                  sx={{
                                    color:
                                      facility.readinessScore >= 90
                                        ? "#4caf50"
                                        : facility.readinessScore >= 80
                                        ? "#2196f3"
                                        : facility.readinessScore >= 70
                                        ? "#ff9800"
                                        : "#f44336",
                                    mr: 1,
                                  }}
                                />
                                <Typography
                                  variant="body2"
                                  sx={{ color: "#003366" }}
                                >
                                  {facility.readinessScore}%
                                </Typography>
                              </Box>
                            </TableCell>
                            <TableCell sx={{ color: "#003366" }}>
                              {facility.bedCapacity}
                            </TableCell>
                            <TableCell sx={{ color: "#003366" }}>
                              {facility.emergencyStaff}
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                </Paper>
              </Grid>

              {/* Readiness Distribution */}
              <Grid item xs={12} md={6}>
                <Paper
                  sx={{
                    p: 3,
                    backgroundColor: "rgba(255, 255, 255, 0.9)",
                    height: "100%",
                  }}
                >
                  <Typography
                    variant="h6"
                    gutterBottom
                    sx={{ color: "#003366" }}
                  >
                    Readiness Score Distribution
                  </Typography>
