                    <BarChart
                      data={facilityData}
                      margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
                    >
                      <CartesianGrid strokeDasharray="3 3" stroke="#ccc" />
                      <XAxis dataKey="name" tick={false} stroke="#003366" />
                      <YAxis domain={[0, 100]} stroke="#003366" />
                      <RechartsTooltip
                        formatter={(value, name, props) => [
                          `${value}%`,
                          "Readiness Score",
                        ]}
                        labelFormatter={(value) => facilityData[value].name}
                        contentStyle={{
                          backgroundColor: "rgba(255, 255, 255, 0.95)",
                          color: "#003366",
                        }}
                      />
                      <Bar dataKey="readinessScore" fill="#FF6600" />
                    </BarChart>
                  </ResponsiveContainer>
                  <Typography variant="body2" sx={{ color: "#003366", mt: 2 }}>
                    Memorial Hermann Hospital has the highest readiness score at
                    92%, while Bayshore Medical Center has the lowest at 75%.
                  </Typography>
                </Paper>
              </Grid>

              {/* State Distribution */}
              <Grid item xs={12} md={6}>
                <Paper
                  sx={{
                    p: 3,
                    backgroundColor: "rgba(255, 255, 255, 0.9)",
                    height: "100%",
                  }}
                >
                  <Typography
                    variant="h6"
                    gutterBottom
                    sx={{ color: "#003366" }}
                  >
                    Facilities by State
                  </Typography>
                  <ResponsiveContainer width="100%" height={300}>
                    <PieChart>
                      <Pie
                        data={stateDistribution}
                        cx="50%"
                        cy="50%"
                        labelLine={false}
                        label={({ name, percent }) =>
                          `${name}: ${(percent * 100).toFixed(0)}%`
                        }
                        outerRadius={80}
                        fill="#8884d8"
                        dataKey="value"
                      >
                        {stateDistribution.map((entry, index) => (
                          <Cell
                            key={`cell-${index}`}
                            fill={BLUE_COLORS[index % BLUE_COLORS.length]}
                          />
                        ))}
                      </Pie>
                      <RechartsTooltip
                        contentStyle={{
                          backgroundColor: "rgba(255, 255, 255, 0.95)",
                          color: "#003366",
                        }}
                      />
                    </PieChart>
                  </ResponsiveContainer>
                  <Box
                    sx={{ display: "flex", justifyContent: "center", mt: 2 }}
                  >
                    <Button
                      variant="contained"
                      sx={{
                        backgroundColor: "#FF6600",
                        "&:hover": { backgroundColor: "#e65c00" },
                        color: "#ffffff",
                      }}
                    >
                      View State-by-State Report
                    </Button>
                  </Box>
                </Paper>
              </Grid>
            </Grid>
          )}

          {/* Tab 3: Resource Allocation */}
          {tabValue === 2 && (
            <Grid container spacing={3}>
              {/* Resource Cards */}
              <Grid item xs={12}>
                <Grid container spacing={2}>
                  <Grid item xs={12} sm={6} md={3}>
                    <Card sx={{ backgroundColor: "#004080", color: "#ffffff" }}>
                      <CardContent sx={{ textAlign: "center" }}>
                        <Typography
                          variant="h6"
                          gutterBottom
                          sx={{ color: "#ffffff" }}
                        >
                          Emergency Supplies
                        </Typography>
                        <Typography
                          variant="h3"
                          sx={{ color: "#FF6600", fontWeight: "bold" }}
                        >
                          92%
                        </Typography>
                        <Typography variant="body2" sx={{ color: "#f8f8f8" }}>
                          of target inventory
                        </Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                  <Grid item xs={12} sm={6} md={3}>
                    <Card sx={{ backgroundColor: "#004080", color: "#ffffff" }}>
                      <CardContent sx={{ textAlign: "center" }}>
                        <Typography
                          variant="h6"
                          gutterBottom
                          sx={{ color: "#ffffff" }}
                        >
                          Backup Power
                        </Typography>
                        <Typography
                          variant="h3"
                          sx={{ color: "#FF6600", fontWeight: "bold" }}
                        >
                          100%
                        </Typography>
                        <Typography variant="body2" sx={{ color: "#f8f8f8" }}>
                          of facilities equipped
                        </Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                  <Grid item xs={12} sm={6} md={3}>
                    <Card sx={{ backgroundColor: "#004080", color: "#ffffff" }}>
                      <CardContent sx={{ textAlign: "center" }}>
                        <Typography
                          variant="h6"
                          gutterBottom
                          sx={{ color: "#ffffff" }}
                        >
                          Water Reserves
                        </Typography>
                        <Typography
                          variant="h3"
                          sx={{ color: "#FF6600", fontWeight: "bold" }}
                        >
                          78%
                        </Typography>
                        <Typography variant="body2" sx={{ color: "#f8f8f8" }}>
                          of target capacity
                        </Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                  <Grid item xs={12} sm={6} md={3}>
                    <Card sx={{ backgroundColor: "#004080", color: "#ffffff" }}>
                      <CardContent sx={{ textAlign: "center" }}>
                        <Typography
                          variant="h6"
                          gutterBottom
                          sx={{ color: "#ffffff" }}
                        >
                          Evacuation Routes
                        </Typography>
                        <Typography
                          variant="h3"
                          sx={{ color: "#FF6600", fontWeight: "bold" }}
                        >
                          10/10
                        </Typography>
                        <Typography variant="body2" sx={{ color: "#f8f8f8" }}>
                          facilities with verified plans
                        </Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                </Grid>
              </Grid>

              {/* Resource Charts */}
              <Grid item xs={12} md={6}>
                <Paper
                  sx={{
                    p: 3,
                    backgroundColor: "rgba(255, 255, 255, 0.9)",
                    height: "100%",
                  }}
                >
                  <Typography
                    variant="h6"
                    gutterBottom
                    sx={{ color: "#003366" }}
                  >
                    Bed Capacity Utilization
                  </Typography>
                  <Box
                    sx={{
                      display: "flex",
                      justifyContent: "center",
                      alignItems: "center",
                      height: 300,
                    }}
                  >
                    <ResponsiveContainer width="100%" height="100%">
                      <PieChart>
                        <Pie
                          data={resourceDistribution}
                          cx="50%"
                          cy="50%"
                          labelLine={true}
                          label={({ name, value, percent }) =>
                            `${name}: ${value} (${(percent * 100).toFixed(0)}%)`
                          }
                          outerRadius={100}
                          fill="#8884d8"
                          dataKey="value"
                        >
                          <Cell fill="#FF6600" />
                          <Cell fill="#e6f7ff" />
                        </Pie>
                        <RechartsTooltip
                          contentStyle={{
                            backgroundColor: "rgba(255, 255, 255, 0.95)",
                            color: "#003366",
                          }}
                        />
                      </PieChart>
                    </ResponsiveContainer>
                  </Box>
                  <Typography
                    variant="body2"
                    sx={{ color: "#003366", mt: 2, textAlign: "center" }}
                  >
                    5,861 total beds across high-risk facilities with 4,361
                    (74%) currently available
                  </Typography>
                </Paper>
              </Grid>

              <Grid item xs={12} md={6}>
                <Paper
                  sx={{
                    p: 3,
                    backgroundColor: "rgba(255, 255, 255, 0.9)",
                    height: "100%",
                    display: "flex",
                    flexDirection: "column",
                  }}
                >
                  <Typography
                    variant="h6"
                    gutterBottom
                    sx={{ color: "#003366" }}
                  >
                    Critical Resources by Facility
                  </Typography>
                  <TableContainer sx={{ flex: 1 }}>
                    <Table size="small">
                      <TableHead>
                        <TableRow>
                          <TableCell
                            sx={{ fontWeight: "bold", color: "#003366" }}
                          >
                            Facility
                          </TableCell>
                          <TableCell
                            sx={{ fontWeight: "bold", color: "#003366" }}
                          >
                            Emergency Power
                          </TableCell>
                          <TableCell
                            sx={{ fontWeight: "bold", color: "#003366" }}
                          >
                            Water Supply
                          </TableCell>
                          <TableCell
                            sx={{ fontWeight: "bold", color: "#003366" }}
                          >
                            Medical O₂
                          </TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {facilityData.slice(0, 5).map((facility) => (
                          <TableRow key={facility.name}>
                            <TableCell sx={{ color: "#003366" }}>
                              {facility.name}
                            </TableCell>
                            <TableCell>
                              <Chip
                                size="small"
                                label={
                                  Math.random() > 0.2
                                    ? "Operational"
                                    : "Limited"
                                }
                                color={
                                  Math.random() > 0.2 ? "success" : "warning"
                                }
                                sx={{
                                  color: "#ffffff",
                                  "& .MuiChip-label": { color: "#ffffff" },
                                }}
                              />
                            </TableCell>
                            <TableCell>
                              <Chip
                                size="small"
                                label={
                                  Math.random() > 0.3 ? "Sufficient" : "Low"
                                }
                                color={
                                  Math.random() > 0.3 ? "success" : "warning"
                                }
                                sx={{
                                  color: "#ffffff",
                                  "& .MuiChip-label": { color: "#ffffff" },
                                }}
                              />
                            </TableCell>
                            <TableCell>
                              <Chip
                                size="small"
                                label={
                                  Math.random() > 0.1 ? "Adequate" : "Critical"
                                }
                                color={
                                  Math.random() > 0.1 ? "success" : "error"
                                }
                                sx={{
                                  color: "#ffffff",
                                  "& .MuiChip-label": { color: "#ffffff" },
                                }}
                              />
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                  <Box
                    sx={{ mt: 2, display: "flex", justifyContent: "flex-end" }}
                  >
                    <Button
                      variant="outlined"
                      sx={{
                        borderColor: "#FF6600",
                        color: "#FF6600",
                        "&:hover": {
                          borderColor: "#e65c00",
                          backgroundColor: "rgba(255, 102, 0, 0.1)",
                        },
                      }}
                    >
                      View Full Report
                    </Button>
                  </Box>
                </Paper>
              </Grid>

              {/* Recommended Actions */}
              <Grid item xs={12}>
                <Paper
                  sx={{
                    p: 3,
                    backgroundColor: "rgba(255, 255, 255, 0.9)",
                  }}
                >
                  <Typography
                    variant="h6"
                    gutterBottom
                    sx={{ color: "#003366" }}
                  >
                    Recommended Resource Actions
                  </Typography>
                  <List>
                    <ListItem>
                      <ListItemIcon>
                        <Box
                          sx={{
                            bgcolor: "#f44336",
                            borderRadius: "50%",
                            width: 12,
                            height: 12,
                          }}
                        />
                      </ListItemIcon>
                      <ListItemText
                        primary={
                          <Typography sx={{ color: "#003366" }}>
                            North Florida Regional Medical Center requires
                            immediate water resupply
                          </Typography>
                        }
                        secondary={
                          <Typography sx={{ color: "#555555" }}>
                            Current levels at 32% of capacity
                          </Typography>
                        }
                      />
                    </ListItem>
                    <ListItem>
                      <ListItemIcon>
                        <Box
                          sx={{
                            bgcolor: "#ff9800",
                            borderRadius: "50%",
                            width: 12,
                            height: 12,
                          }}
                        />
                      </ListItemIcon>
                      <ListItemText
                        primary={
                          <Typography sx={{ color: "#003366" }}>
                            Memorial Hermann Hospital emergency power generators
                            need maintenance
                          </Typography>
                        }
                        secondary={
                          <Typography sx={{ color: "#555555" }}>
                            Last service date: 6 months ago
                          </Typography>
                        }
                      />
                    </ListItem>
                    <ListItem>
                      <ListItemIcon>
                        <Box
                          sx={{
                            bgcolor: "#ff9800",
                            borderRadius: "50%",
                            width: 12,
                            height: 12,
                          }}
                        />
                      </ListItemIcon>
                      <ListItemText
                        primary={
                          <Typography sx={{ color: "#003366" }}>
                            Bayshore Medical Center requires additional medical
                            oxygen tanks
                          </Typography>
                        }
                        secondary={
                          <Typography sx={{ color: "#555555" }}>
                            Current supply at 45% of recommended levels
                          </Typography>
                        }
                      />
                    </ListItem>
                    <ListItem>
                      <ListItemIcon>
                        <Box
                          sx={{
                            bgcolor: "#4caf50",
                            borderRadius: "50%",
                            width: 12,
                            height: 12,
                          }}
                        />
                      </ListItemIcon>
                      <ListItemText
                        primary={
                          <Typography sx={{ color: "#003366" }}>
                            All Florida facilities need to verify evacuation
                            plans before hurricane season
                          </Typography>
                        }
                        secondary={
                          <Typography sx={{ color: "#555555" }}>
                            Deadline: May 31, 2024
                          </Typography>
                        }
                      />
                    </ListItem>
                  </List>
                  <Box
                    sx={{ mt: 2, display: "flex", justifyContent: "center" }}
                  >
                    <Button
                      variant="contained"
                      sx={{
                        backgroundColor: "#FF6600",
                        "&:hover": { backgroundColor: "#e65c00" },
                        color: "#ffffff",
                      }}
                    >
                      Generate Resource Allocation Plan
                    </Button>
                  </Box>
                </Paper>
              </Grid>
            </Grid>
          )}
        </>
      )}
    </Container>
  );
}

export default LocalDashboard;
